import{_ as a}from"./chunks/SynthPage.Cc7Bfnt_.js";import{C as s,h as n,E as r,P as i,Q as l,u as p,H as h,f as c}from"./chunks/framework.gmJ0nyoV.js";import"./chunks/useItems.BiqRekTS.js";import"./chunks/theme.BqyWToM9.js";import"./chunks/index.Bz62Jzae.js";const w=JSON.parse('{"title":"Web-synth","description":"Synth page","frontmatter":{"title":"Web-synth","description":"Synth page","dynamic":true,"page_type":"synth"},"headers":[],"params":{"url":"https://choptop84.github.io/abyssbox-app/","date_created":"2025-01-29T17:14:36.989Z","title":"AbyssBox","slug":"abyss-box","author":"choptop84","author_link":"https://choptop84.github.io/","description":"AbyssBox is a mod of BeepBox developed by choptop84 and UltraBox is a mod of BeepBox developed by Main and Neptendo.","tags":["DAW","FM"],"archive_link":null,"archive":false,"iframe":true,"cover":"f4c948ef-3b7a-4d16-bf28-f85ebcc13b06","id":67,"sort":58,"clicks":2,"public":false,"is_new":true,"playtronica_compatible":false,"stats":[59]},"relativePath":"abyss-box/index.md","filePath":"[slug]/index.md","lastUpdated":1736665814000}'),d={name:"abyss-box/index.md"},k=Object.assign(d,{setup(u){const{params:t,frontmatter:m}=s();return(y,e)=>{const o=a;return c(),n("div",null,[r(o,i(l(p(t))),null,16),e[0]||(e[0]=h("<p>AbyssBox is a Modification of Ultrabox by choptop84, Ultrabox is made by Neptendo and Main, with assistance from community members! Ultrabox takes elements from most of the beepbox mods available (see credits) and puts them in one convenient package.</p><p>AbyssBox adds many improvements to the UI for both PC and Mobile. Over 20 themes, and 5 layouts! Plus many more other miscellaneous changes that improve the user experience. Though that isn&#39;t all AbyssBox adds. There are many more changes and improvements all over the app.</p><p>All song data is contained in the URL at the top of your browser. When you make changes to the song, the URL is updated to reflect your changes. When you are satisfied with your song, just copy and paste the URL to save and share your song!</p><p>Though you should instead click &#39;File&#39; and then &#39;Shorten Song Url&#39;, it makes the link more managable! Instructions</p><p>You can add or remove notes by clicking on the gray rows at the top. AbyssBox automatically plays the notes out loud for you. Try it!</p><p>Notes go into patterns, and you can edit one pattern at a time. Those numbered boxes at the bottom of the editor are the different patterns you can edit. Click the boxes to move to a different part of the song, or click the arrows on the currently selected box to swap which pattern is played during that part of the song.</p><p>AbyssBox can play several rows of patterns simultaneously, and each row has its own set of patterns. Most rows can play melodies or harmonies, but the bottom row is for drums.</p><p>All song data is contained in the URL at the top of your browser. When you make changes to the song, the URL is updated to reflect your changes. When you are satisfied with your song, just copy and paste the URL to save and share your song!</p><p>When AbyssBox has focus (click on its interface above), you can use these keyboard shortcuts:</p><ul><li>Spacebar: play or pause the song</li><li>Shift Spacebar: play from mouse location</li><li>Z: undo, Y or Shift Z: redo</li><li>C: copy pattern from selection</li><li>V: paste pattern into selection</li><li>0-9: assign pattern number to selection</li><li>Arrows: move selection</li><li>Ctrl + Arrows: rearrange channels</li><li>[ ]: move playhead backward or forward</li><li>F/H: move to First or Highlighted pattern</li><li>Shift &amp; Drag: select part of a pattern (long press on touch screen)</li><li>These are just a few shortcuts, check AbyssBox&#39;s edit menu for more!</li></ul><p>In the note pattern editor, you can click and drag horizontally on a note to adjust its duration. You can also click above or below an existing note to add more notes to be played simultaneously, which is called a chord.</p><p>ADVANCED: Drag vertically from an existing note to bend its pitch, or drag vertically from above or below the note to adjust its volume. You can perform fine volume adjustment if you hold Control while doing this!</p><p>ADVANCED: Click and drag on the pattern grid to make a selection. You can then press C/V to mass copy/paste parts of your song.</p><p>AbyssBox has many more features. Try playing with the buttons and menus on the right side to find out what it can do! You can also click on the label next to each option for a description of what it does.</p>",14))])}}});export{w as __pageData,k as default};
